parameters.parameter:
var lld = [];
var lines = value.split(",");
var lines_num = lines.length;
for (i = 0; i < lines_num; i++)
{
  var row = {};
  row["{#INT}"] = lines[i]
  lld.push(row);
}
return JSON.stringify(lld);

